import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedDates, setBookedDates] = useState([{ "FromDate": "2023-05-11", "ToDate": "2023-05-13" }, { "FromDate": "2023-05-14", "ToDate": "2023-05-15" }]);

    const handleFromDateChange = (date) => {
        setFromDate(date);
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        // Thực hiện logic booking tại đây
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isBookedDate = (date) => {
        return bookedDates.some((bookedDate) => isSameDay(date, bookedDate));
    };

    const isSameDay = (date1, date2) => {
        return (
            date1.getDate() === date2.getDate() &&
            date1.getMonth() === date2.getMonth() &&
            date1.getFullYear() === date2.getFullYear()
        );
    };

    const excludeBookedDates = bookedDates.filter((bookedDate) => {
        return fromDate && toDate ? isDateInRange(bookedDate, fromDate, toDate) : true;
    });

    const isDateInRange = (date, rangeStart, rangeEnd) => {
        return date >= rangeStart && date <= rangeEnd;
    };

    const renderDay = (day) => {
        const isBooked = isBookedDate(day);

        const dayClassName = isBooked ? 'booked-day' : '';

        return (
            <div className={`custom-day ${dayClassName}`}>
                {day.getDate()}
            </div>
        );
    };

    return (
        <div>
            <h3>Booking Form</h3>
            <div>
                <label htmlFor="fromDate">From Date:</label>
                <DatePicker
                    id="fromDate"
                    selected={fromDate}
                    onChange={handleFromDateChange}
                    renderDay={renderDay}
                    excludeDates={excludeBookedDates}
                />
            </div>
            <div>
                <label htmlFor="toDate">To Date:</label>
                <DatePicker
                    id="toDate"
                    selected={toDate}
                    onChange={handleToDateChange}
                    renderDay={renderDay}
                    excludeDates={excludeBookedDates}
                />
            </div>
            <button onClick={handleBooking}>Booking</button>
        </div>
    );
}

export default BookingForm;
