import axios from 'axios';
import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import styled from 'styled-components';

function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedDates, setBookedDates] = useState([]);

    useEffect(() => {
        let config = {
            method: 'get',
            maxBodyLength: Infinity,
            url: `http://localhost/car/KLTN/carrentalAPI/Get_DateBooking.php?vid=2`,
            headers: {},
        };

        axios
            .request(config)
            .then((response) => {
                if (response.data.status === '200') {
                    setBookedDates(response.data.date);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    }, []);

    const handleFromDateChange = (date) => {
        setFromDate(date);
        setToDate(null); // Reset toDate when changing fromDate
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isBookedDate = (date) => {
        return bookedDates.includes(date);
    };

    const renderDay = (day) => {
        const isBooked = isBookedDate(day.toISOString().split('T')[0]);
        const isFutureDay = day > new Date();

        const dayClassName = isBooked ? 'booked-day' : '';
        const additionalClass = isFutureDay ? '' : 'disabled-day';

        return (
            <div className={`custom-day ${dayClassName} ${additionalClass}`}>
                {day.getDate()}
            </div>
        );
    };

    const excludeDates = bookedDates.map((date) => new Date(date));
    const today = new Date();
    const minDate = new Date(today.getFullYear(), today.getMonth(), today.getDate());

    const filterPassedDate = (date) => {
        return (date > fromDate && date > today) || !fromDate; // Hiển thị ngày sau fromDate hoặc không có fromDate và không hiển thị ngày trước ngày hiện tại
    };

    return (
        <BookDiv>
            <div className="form-group">
                <label>From Date:</label>
                <DatePicker
                    selectsStart
                    selected={fromDate}
                    minDate={minDate}
                    startDate={fromDate}
                    endDate={toDate}
                    onChange={handleFromDateChange}
                    renderDay={renderDay}
                    excludeDates={excludeDates}
                    className="form-control"
                    name="fromdate"
                    placeholderText="Chọn ngày nhận xe"
                />
            </div>

            <div className="form-group">
                <label>To Date:</label>
                <DatePicker
                    selectsEnd
                    selected={toDate}
                    minDate={fromDate || minDate} // Chỉ cho phép chọn ngày từ fromDate trở đi
                    startDate={fromDate}
                    endDate={toDate}
                    onChange={handleToDateChange}
                    renderDay={renderDay}
                    excludeDates={excludeDates}
                    className="form-control"
                    name="todate"
                    placeholderText="Chọn ngày trả xe"
                    filterDate={filterPassedDate}
                />
            </div>

            <button onClick={handleBooking}>Booking</button>
        </BookDiv>
    );
}

const BookDiv = styled.div`
  .form-group {
    margin-bottom: 1rem;
  }

  .form-label {
    margin-bottom: 0.5rem;
  }

  .form-control {
    border: 1px solid #ced4da;
    border-radius: 0.25rem;
    padding: 0.375rem 0.75rem;
    line-height: 1.5;
  }

  .custom-day.disabled-day {
    background-color: #f8d7da;
    color: #721c24;
    cursor: not-allowed;
  }
`;

export default BookingForm;
