import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedRanges, setBookedRanges] = useState([]);

    useEffect(() => {
        // Gọi API để lấy danh sách ngày đã được thuê
        fetchBookedRanges();
    }, []);

    const fetchBookedRanges = () => {
        // Gọi API và nhận phản hồi
        // Đoạn mã này chỉ là một mô phỏng, bạn cần thay thế bằng kết nối thực tế đến API của bạn
        const apiResponse = {
            date: [
                { FromDate: '2023-05-11', ToDate: '2023-05-13' },
                { FromDate: '2023-05-14', ToDate: '2023-05-15' },
            ],
            status: '200',
        };

        // Kiểm tra phản hồi từ API
        if (apiResponse.status === '200') {
            const { date } = apiResponse;
            const bookedRangesFromAPI = date.map((booking) => {
                return {
                    fromDate: new Date(booking.FromDate),
                    toDate: new Date(booking.ToDate),
                };
            });
            setBookedRanges(bookedRangesFromAPI);
        }
    };

    const handleFromDateChange = (date) => {
        setFromDate(date);
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        // Thực hiện logic booking tại đây
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isDateInRange = (date, rangeStart, rangeEnd) => {
        const currentDate = new Date(date);
        const startDate = new Date(rangeStart);
        const endDate = new Date(rangeEnd);

        return currentDate >= startDate && currentDate <= endDate;
    };

    const renderDay = (day) => {
        const isBooked = bookedRanges.some((range) =>
            isDateInRange(day, range.fromDate, range.toDate)
        );

        const dayClassName = isBooked ? 'booked-day' : '';

        return (
            <div className={`custom-day ${dayClassName}`}>
                {day.getDate()}
            </div>
        );
    };

    return (
        <div>
            <h3>Booking Form</h3>
            <div>
                <label htmlFor="fromDate">From Date:</label>
                <DatePicker
                    id="fromDate"
                    selected={fromDate}
                    onChange={handleFromDateChange}
                    renderDay={renderDay}
                    excludeDates={bookedRanges.map((range) => range.fromDate)}
                    startDate={fromDate}
                    endDate={toDate}
                    selectsRange
                />
            </div>
            <div>
                <label htmlFor="toDate">To Date:</label>
                <DatePicker
                    id="toDate"
                    selected={toDate}
                    onChange={handleToDateChange}
                    renderDay={renderDay}

                    excludeDates={bookedRanges.map((range) => range.toDate)}
                    startDate={fromDate}
                    endDate={toDate}
                    selectsRange
                />
            </div>
            <button onClick={handleBooking}>Booking</button>
        </div>
    );
}

export default BookingForm;




