import React, { useContext, useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { InfoContext } from '../../contextProvider/ProviderInfo';
import Mic from '../Header/microphone-black-shape.svg';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

function App() {
    const { searchValue } = useContext(InfoContext);
    const location = useLocation();
    const searchRedirect = useNavigate();

    const [transcript, setTranscript] = useState('');
    const [isListening, setIsListening] = useState(false);
    const [showModal, setShowModal] = useState(false);
    const [showErrorMessage, setShowErrorMessage] = useState(false);

    const startRecognition = () => {
        const recognition = new window.webkitSpeechRecognition();
        recognition.continuous = false;
        recognition.interimResults = false;
        recognition.lang = 'vi-VN';

        recognition.onstart = () => {
            setIsListening(true);
            setShowErrorMessage(false);
        };

        recognition.onresult = (event) => {
            const speechToText = event.results[0][0].transcript;
            setTranscript(speechToText);
        };

        recognition.onerror = (event) => {
            console.error('Lỗi nhận dạng giọng nói:', event.error);
            setShowErrorMessage(true);
        };

        recognition.onend = () => {
            recognition.stop();
            setIsListening(false);
        };

        recognition.start();
    };

    useEffect(() => {
        if (transcript !== '') {
            searchValue.set('searchValue', { search: transcript }, { path: '/' });

            if (location.pathname === '/Carlisting') {
                window.location.reload();
            } else {
                searchRedirect('/Carlisting');
            }
        }
    }, [transcript, searchValue, location.pathname, searchRedirect]);

    const handleStartRecognition = () => {
        setIsListening(true);
        setShowModal(true);
        startRecognition();
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    return (
        <div>
            <h1>Ứng dụng Nhận dạng Giọng nói</h1>
            {!isListening && (
                <Button variant="primary" onClick={handleStartRecognition}>
                    Bắt đầu Nhận dạng
                </Button>
            )}
            <Modal show={showModal} onHide={handleCloseModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Xin mời nói...</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {showErrorMessage ? (
                        <p>Vui lòng kiểm tra mic và âm lượng của bạn</p>
                    ) : (
                        <div className="voice">
                            <img
                                className={`microphone ${isListening && 'isListening'}`}
                                src={Mic}
                                alt="microphone"
                            />
                        </div>
                    )}
                </Modal.Body>
            </Modal>
            <p>{transcript}</p>
        </div>
    );
}

export default App;
