import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedDates, setBookedDates] = useState([[
        { FromDate: '2023-05-11', ToDate: '2023-05-13' },
        { FromDate: '2023-05-14', ToDate: '2023-05-15' },
    ]]);

    useEffect(() => {
        // Gọi API để lấy danh sách ngày đã được thuê
        fetchBookedDates();
    }, []);

    // const fetchBookedDates = async () => {
    //     try {
    //         const response = await fetch('YOUR_API_ENDPOINT');
    //         const data = await response.json();

    //         if (data.status === '200') {
    //             const { date } = data;
    //             setBookedDates(date);
    //         }
    //     } catch (error) {
    //         console.error('Error fetching booked dates:', error);
    //     }
    // };

    const handleFromDateChange = (date) => {
        setFromDate(date);
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isBookedDate = (date) => {
        return bookedDates.some((booking) => isDateInRange(date, booking.FromDate, booking.ToDate));
    };

    const isDateInRange = (date, rangeStart, rangeEnd) => {
        return date >= new Date(rangeStart) && date <= new Date(rangeEnd);
    };

    const renderDay = (day) => {
        const isBooked = isBookedDate(day);

        const dayClassName = isBooked ? 'booked-day' : '';

        return (
            <div className={`custom-day ${dayClassName}`}>
                {day.getDate()}
            </div>
        );
    };

    const excludeDatesInRange = bookedDates.reduce((excludeDates, booking) => {
        const start = new Date(booking.FromDate);
        const end = new Date(booking.ToDate);

        const currentDay = new Date(start);
        while (currentDay <= end) {
            excludeDates.push(new Date(currentDay));
            currentDay.setDate(currentDay.getDate() + 1);
        }

        return excludeDates;
    }, []);

    return (
        <div>
            <h3>Booking Form</h3>
            <div>
                <label htmlFor="fromDate">From Date:</label>
                <DatePicker
                    id="fromDate"
                    selected={fromDate}
                    onChange={handleFromDateChange}
                    renderDay={renderDay}
                    excludeDates={excludeDatesInRange}
                />
            </div>
            <div>
                <label htmlFor="toDate">To Date:</label>
                <DatePicker
                    id="toDate"
                    selected={toDate}
                    onChange={handleToDateChange}
                    renderDay={renderDay}
                    excludeDates={excludeDatesInRange}
                />
            </div>
            <button onClick={handleBooking}>Book</button>
        </div>
    );
}

export default BookingForm;
