import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import styled from 'styled-components';
function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedDates, setBookedDates] = useState([
        new Date('2023-05-11'),
        new Date('2023-05-12'),
        new Date('2023-05-13'),
        new Date('2023-05-14'),
        new Date('2023-05-15'),
    ]);

    const handleFromDateChange = (date) => {
        setFromDate(date);
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        // Thực hiện logic booking tại đây
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isBookedDate = (date) => {
        return bookedDates.some((bookedDate) => isSameDay(date, bookedDate));
    };

    const isSameDay = (date1, date2) => {
        return (
            date1.getDate() === date2.getDate() &&
            date1.getMonth() === date2.getMonth() &&
            date1.getFullYear() === date2.getFullYear()
        );
    };

    const renderDay = (day) => {
        const isBooked = isBookedDate(day);

        const dayClassName = isBooked ? 'booked-day' : '';

        return (
            <div className={`custom-day ${dayClassName}`}>
                {day.getDate()}
            </div>
        );
    };

    return (
        <div>
            <h3>Booking Form</h3>
            <div>
                <label htmlFor="fromDate">From Date:</label>
                <DatePicker
                    id="fromDate"
                    selected={fromDate}
                    onChange={handleFromDateChange}
                    renderDay={renderDay}
                />
            </div>
            <div>
                <label htmlFor="toDate">To Date:</label>
                <DatePicker
                    id="toDate"
                    selected={toDate}
                    onChange={handleToDateChange}
                    renderDay={renderDay}
                />
            </div>
            <button onClick={handleBooking}>Booking</button>
        </div>
    );
}
const TestDiv = styled.div`

.custom-day {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 2rem;
  height: 2rem;
  border-radius: 50%;
  margin: 0.2rem;
}

.booked-day {
  background-color: red;
  color: white;
}`
export default BookingForm;
