    return (
        <div>
            <Header />
            <div>
                <div>
                    <div className="row">
                        <div className="col-lg-4 welcome-right">
                            <img src="http://localhost/car/thueoto/uploads/r.jpg" className="img-fluid" style={{ width: 1000 }} alt=" " />
                        </div>
                        <div className="col-lg-8 welcome-left">
                            <h5><a href="index.php?quanly=chitiettin&id_tin=<?php echo $row_baiviet['baiviet_id'] ?>">{/*?php echo $row_baiviet['tenbaiviet'] ?*/}</a></h5>
                            <h4 className="my-sm-3 my-2">{/*?php echo $row_baiviet['tomtat'] ?*/}</h4>
                        </div>
                    </div><br />
                </div>

            </div>


            <Footer />
        </div>
    );
};
export default TinTuc;
