import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import axios from 'axios';
function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedDates, setBookedDates] = useState([]);

    useEffect(() => {
        let config = {
            method: "get",
            maxBodyLength: Infinity,
            url: `http://localhost/car/KLTN/carrentalAPI/Get_DateBooking.php?vid=1`,
            headers: {},
        };

        axios
            .request(config)
            .then((response) => {
                // console.log(JSON.stringify(response));
                // setVehiclesBrand(response.data.carDetail[0].VehiclesBrand)
                if (response.data.status === "200") {
                    const bookedDateRanges = data.date.map((booking) => {
                        return {
                            fromDate: new Date(booking.FromDate),
                            toDate: new Date(booking.ToDate),
                        };
                    });
                    setBookedDates(bookedDateRanges);
                }
            })
            .catch((error) => {
                console.log(error);
            });
        fetchBookedDates();
    }, []);
    const fetchBookedDates = async () => {
        try {
            // Gọi API và nhận phản hồi
            // Đoạn mã này chỉ là một mô phỏng, bạn cần thay thế bằng kết nối thực tế đến API của bạn
            const response = await fetch('API_ENDPOINT_URL');
            const data = await response.json();

            // Kiểm tra phản hồi từ API
            if (data.status === '200') {
                const bookedDateRanges = data.date.map((booking) => {
                    return {
                        fromDate: new Date(booking.FromDate),
                        toDate: new Date(booking.ToDate),
                    };
                });
                setBookedDates(bookedDateRanges);
            }
        } catch (error) {
            console.error('Error fetching booked dates:', error);
        }
    };

    const handleFromDateChange = (date) => {
        setFromDate(date);
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        // Thực hiện logic booking tại đây
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isBookedDate = (date) => {
        return bookedDates.some((booking) => isDateInRange(date, booking.fromDate, booking.toDate));
    };

    const isDateInRange = (date, rangeStart, rangeEnd) => {
        return date >= rangeStart && date <= rangeEnd;
    };

    const renderDay = (day) => {
        const isBooked = isBookedDate(day);

        const dayClassName = isBooked ? 'booked-day' : '';

        return (
            <div className={`custom-day ${dayClassName}`}>
                {day.getDate()}
            </div>
        );
    };

    return (
        <div>
            <h3>Booking Form</h3>
            <div>
                <label htmlFor="fromDate">From Date:</label>
                <DatePicker
                    id="fromDate"
                    selected={fromDate}
                    onChange={handleFromDateChange}
                    renderDay={renderDay}
                    excludeDates={bookedDates.map((booking) => booking.fromDate)}
                    minDate={new Date()} // Chỉ cho phép chọn từ ngày hiện tại trở đi
                    maxDate={toDate || null} // Tối đa là toDate, nếu đã chọn
                />
            </div>
            <div>
                <label
                    htmlFor="toDate">To Date:</label>
                <DatePicker
                    id="toDate"
                    selected={toDate}
                    onChange={handleToDateChange}
                    renderDay={renderDay}
                    excludeDates={bookedDates.map((booking) => booking.toDate)}
                    minDate={fromDate || new Date()} // Tối thiểu là fromDate, nếu đã chọn
                    maxDate={null} // Không giới hạn tối đa
                />
            </div>
            <button onClick={handleBooking}>Booking</button>
        </div>
    );
}

export default BookingForm;
