import React, { useContext, useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { InfoContext } from '../../contextProvider/ProviderInfo';

function App() {
    const { searchValue } = useContext(InfoContext);
    const location = useLocation();
    const searchRedirect = useNavigate();

    const [transcript, setTranscript] = useState('');
    const [isListening, setIsListening] = useState(false); // Biến trạng thái cho hiệu ứng cái mic

    const startRecognition = () => {
        const recognition = new window.webkitSpeechRecognition();
        recognition.continuous = false;
        recognition.interimResults = false;
        recognition.lang = 'vi-VN'; // Đặt ngôn ngữ là tiếng Việt

        recognition.onstart = () => {
            setIsListening(true); // Bắt đầu nghe -> Đặt biến trạng thái là true
        };

        recognition.onresult = (event) => {
            const speechToText = event.results[0][0].transcript;
            setTranscript(speechToText);
        };

        recognition.onend = () => {
            recognition.stop();
            setIsListening(false); // Kết thúc nghe -> Đặt biến trạng thái là false
        };

        recognition.start();
    };

    useEffect(() => {
        if (transcript !== "") {
            searchValue.set("searchValue", { search: transcript }, { path: "/" });

            if (location.pathname === "/Carlisting") {
                window.location.reload();
            } else {
                searchRedirect("/Carlisting");
            }
        }
    }, [transcript, searchValue, location.pathname, searchRedirect]);

    return (
        <div>
            <h1>Ứng dụng Nhận dạng Giọng nói</h1>
            <button onClick={startRecognition}>Bắt đầu Nhận dạng</button>
            <div>
                {isListening ? <img src="mic-active.png" alt="Microphone Active" /> : <img src="mic-inactive.png" alt="Microphone Inactive" />}
            </div>
            <p>{transcript}</p>
        </div>
    );
}

export default App;
