import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BookingForm() {
    const [selectedDate, setSelectedDate] = useState(null);
    const [bookedDates, setBookedDates] = useState([
        new Date('2023-05-11'),
        new Date('2023-05-12'),
        new Date('2023-05-13'),
        new Date('2023-05-14'),
        new Date('2023-05-15'),
    ]);

    const isBookedDate = (date) => {
        return bookedDates.some((bookedDate) => isSameDay(date, bookedDate));
    };

    const isSameDay = (date1, date2) => {
        return (
            date1.getDate() === date2.getDate() &&
            date1.getMonth() === date2.getMonth() &&
            date1.getFullYear() === date2.getFullYear()
        );
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const renderDay = (day, isSelected, isInCurrentMonth, isDayInOtherMonth, isDisabled, isToday) => {
        const isBooked = isBookedDate(day);

        const dayClassName = isBooked ? 'booked-day' : '';

        return (
            <div className={`custom-day ${dayClassName}`}>
                {day.getDate()}
            </div>
        );
    };

    return (
        <div>
            <h3>Booking Form</h3>
            <DatePicker
                selected={selectedDate}
                onChange={handleDateChange}
                renderDay={renderDay}
            />
        </div>
    );
}

export default BookingForm;
