import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import styled from 'styled-components';

function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedDates, setBookedDates] = useState([]);

    useEffect(() => {
        fetchBookedDates();
    }, []);

    const fetchBookedDates = () => {
        const apiResponse = {
            date: [
                "2023-05-16",
                "2023-05-17",
                "2023-05-18",
                "2023-05-19",
                "2023-05-18",
                "2023-05-19",
                "2023-05-20",
                "2023-05-21",
                "2023-05-22",
            ],
            status: '200',
        };

        if (apiResponse.status === '200') {
            setBookedDates(apiResponse.date);
        }
    };

    const handleFromDateChange = (date) => {
        setFromDate(date);
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isBookedDate = (date) => {
        return bookedDates.includes(date);
    };

    const renderDay = (day) => {
        const isBooked = isBookedDate(day.toISOString().split('T')[0]);
        const isFutureDay = day > new Date();

        const dayClassName = isBooked ? 'booked-day' : '';
        const additionalClass = isFutureDay ? '' : 'disabled-day';

        return (
            <div className={`custom-day ${dayClassName} ${additionalClass}`}>
                {day.getDate()}
            </div>
        );
    };

    const excludeDates = bookedDates.map((date) => new Date(date));
    const today = new Date();
    const minDate = new Date(today.getFullYear(), today.getMonth(), today.getDate());

    return (
        <BookDiv >

            <DatePicker
                selectsStart
                selected={fromDate}
                minDate={minDate}
                startDate={fromDate}
                endDate={toDate}
                onChange={handleFromDateChange}
                renderDay={renderDay}
                excludeDates={excludeDates}
                className="form-control"
            />
            <DatePicker
                selectsEnd
                selected={toDate}
                startDate={fromDate}
                endDate={toDate}
                onChange={handleToDateChange}
                renderDay={renderDay}
                excludeDates={excludeDates}
                className="form-control"
            />

            <button onClick={handleBooking}>Booking</button>
        </BookDiv>
    );
}
const BookDiv = styled.div`
  .form-label {
    margin-bottom: 0.5rem;
  }

  .form-control {
    border: 1px solid #ced4da;
    border-radius: 0.25rem;
padding: 0.375rem 0.75rem;
line-height: 1.5;
}

.custom-day.disabled-day {
background-color: #f8d7da;
color: #721c24;
cursor: not-allowed;
}
`;
export default BookingForm;


