import axios from 'axios';
import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function BookingForm() {
    const [fromDate, setFromDate] = useState(null);
    const [toDate, setToDate] = useState(null);
    const [bookedDateRanges, setBookedDateRanges] = useState([]);



    useEffect(() => {
        let config = {
            method: "get",
            maxBodyLength: Infinity,
            url: `http://localhost/car/KLTN/carrentalAPI/Get_DateBooking.php?vid=2`,
            headers: {},
        };

        axios
            .request(config)
            .then((response) => {
                // console.log(JSON.stringify(response));
                // setVehiclesBrand(response.data.carDetail[0].VehiclesBrand)
                if (response.data.status === "200") {
                    const bookedDates = data.date.map((booking) => {
                        return {
                            fromDate: new Date(booking.FromDate),
                            toDate: new Date(booking.ToDate),
                        };
                    });
                    setBookedDateRanges(bookedDates);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    }, []);

    const handleFromDateChange = (date) => {
        setFromDate(date);
    };

    const handleToDateChange = (date) => {
        setToDate(date);
    };

    const handleBooking = () => {
        console.log('From Date:', fromDate);
        console.log('To Date:', toDate);
    };

    const isDateInRange = (date, rangeStart, rangeEnd) => {
        return date >= rangeStart && date <= rangeEnd;
    };

    const renderDay = (day) => {
        const isBooked = bookedDateRanges.some((booking) => isDateInRange(day, booking.fromDate, booking.toDate));
        const isInRange = fromDate && toDate && isDateInRange(day, fromDate, toDate);

        const dayClassName = isBooked ? 'booked-day' : (isInRange ? 'in-range-day' : '');

        return (
            <div className={`custom-day ${dayClassName}`}>
                {day.getDate()}
            </div>
        );
    };

    return (
        <div>
            <h3>Booking Form</h3>
            <div>
                <label htmlFor="fromDate">From Date:</label>
                <DatePicker
                    id="fromDate"
                    selected={fromDate}
                    onChange={handleFromDateChange}
                    renderDay={renderDay}
                />
            </div>
            <div>
                <label htmlFor="toDate">To Date:</label>
                <DatePicker
                    id="toDate"
                    selected={toDate}
                    onChange={handleToDateChange}
                    renderDay={renderDay}
                />
            </div>
            <button onClick={handleBooking}>Booking</button>
        </div>
    );
}

export default BookingForm;





