import axios from 'axios';
import React, { useState, useEffect } from 'react';

const BookingForm = () => {
    const [bookedDates, setBookedDates] = useState([]);
    const [bookingDate, setBookingDate] = useState("");
    const [returnDate, setReturnDate] = useState("");

    useEffect(() => {
        // Gọi API để lấy danh sách ngày đã đặt
        fetch("YOUR_API_ENDPOINT")
            .then(response => response.json())
            .then(data => {
                if (data.status === "200") {
                    setBookedDates(data.date);
                }
            })
            .catch(error => {
                console.error("Error fetching booked dates:", error);
            });
    }, []);
    useEffect(() => {
        let config = {
            method: "get",
            maxBodyLength: Infinity,
            url: `http://localhost/car/KLTN/carrentalAPI/Get_DateBooking.php?vid=3`,
            headers: {},
        };

        axios
            .request(config)
            .then((response) => {

                if (response.data.status === "200") {
                    setBookedDates(response.data.date);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    }, []);

    const handleBookingDateChange = (event) => {
        setBookingDate(event.target.value);
    };

    const handleReturnDateChange = (event) => {
        setReturnDate(event.target.value);
    };

    const isDateBooked = (date) => {
        return bookedDates.includes(date);
    };

    const renderBookingDateOptions = () => {
        const options = [];
        for (let i = 0; i < 7; i++) {
            const currentDate = new Date();
            currentDate.setDate(currentDate.getDate() + i);
            const formattedDate = currentDate.toISOString().split('T')[0];
            if (isDateBooked(formattedDate)) {
                options.push(<option disabled key={formattedDate}>{formattedDate} (Booked)</option>);
            } else {
                options.push(<option key={formattedDate}>{formattedDate}</option>);
            }
        }
        return options;
    };

    const renderReturnDateOptions = () => {
        const options = [];
        const selectedBookingDate = new Date(bookingDate);
        for (let i = 0; i < 7; i++) {
            const currentDate = new Date(selectedBookingDate);
            currentDate.setDate(selectedBookingDate.getDate() + i);
            const formattedDate = currentDate.toISOString().split('T')[0];
            if (isDateBooked(formattedDate)) {
                options.push(<option disabled key={formattedDate}>{formattedDate} (Booked)</option>);
            } else {
                options.push(<option key={formattedDate}>{formattedDate}</option>);
            }
        }
        return options;
    };

    return (
        <div>
            <label htmlFor="booking_date">Ngày đặt:</label>
            <select name="booking_date" value={bookingDate} onChange={handleBookingDateChange}>
                <option value="">Chọn ngày đặt</option>
                {renderBookingDateOptions()}
            </select>

            <label htmlFor="return_date">Ngày trả:</label>
            <select name="return_date" value={returnDate} onChange={handleReturnDateChange}>
                <option value="">Chọn ngày trả</option>
                {renderReturnDateOptions()}
            </select>
        </div>
    );
};

export default BookingForm;





